import React, { useEffect, useState } from 'react';

import './styles.css';

import deleteIcon from '../../assets/images/icons/delete.png';
import gymImage from '../../assets/images/typeIcons/gym.png';
import studiesImage from '../../assets/images/typeIcons/studies.png';
import origamiImage from '../../assets/images/typeIcons/origami.png';
import foodImage from '../../assets/images/typeIcons/food.png';
import businessImage from '../../assets/images/typeIcons/business.png';
import homeImage from '../../assets/images/typeIcons/home.png';
import ideaImage from '../../assets/images/typeIcons/idea.png';
import travelImage from '../../assets/images/typeIcons/travel.png';
import watchImage from '../../assets/images/typeIcons/watch.png';
import petImage from '../../assets/images/typeIcons/pet.png';
import workImage from '../../assets/images/typeIcons/work.png';

export default function Main() {
    const [ tasks, setTasks ] = useState([]);

    const [deleteAnimation, setDeleteAnimation] = useState('');

    function handleDelete(e, id) {
        e.preventDefault();
        //setDeleteAnimation('deleteAnimation');
        console.log('ID' + id);
        const index = tasks.indexOf(tasks[id])
        let newArray = [...tasks];
        console.log(newArray);
        newArray.splice(index, 1);
        console.log(newArray);
        setTasks(newArray);

        setTimeout(() => {
            setDeleteAnimation('');

        }, 2000)
    }

    useEffect(() => {
        setTasks( ...tasks, [
        [ 0, studiesImage, 'Finalizar atividade de cáculo II', '#4E7BFC'],
        [ 1, origamiImage, 'Decorar parede do quarto', '#5ED285'],
        [ 2, foodImage, 'Comprar maça para fazer a torta', '#FD3030'],
        [ 3, businessImage, 'Pagar boleto da fatura do cartão', '#FD5530'],
        [ 4, homeImage, 'Comprar nova lampada', '#7C78FD'],
        [ 5, ideaImage, 'Criar uma planilha para controle financeiro', '#FDC130'],
        [ 6, travelImage, 'Fazer as malas para viagem', '#74DEFF'],
        [ 7, watchImage, 'Entrar em contato com cliente X', '#D4A269'],
        [ 8, petImage, 'Passear com o Bob', '#90FF74'],
        [ 9, workImage, 'Finalizar planilha de clientes', '#6F9379'],
        [ 10, gymImage, 'Fazer exercicío físico', '#F05064'],]);
    }, []);

    return (
        <div className="container">
            <section id="list" className="container">

                {tasks.map( (task, index) => (
                    <div className={`box ${deleteAnimation}`} key={task[0]}>
                        <div className="box-header" style={{background: task[3]}}>
                            <img src={task[1]} alt='studiesImage' />
                        </div>
                        <div className="box-body">
                            <p>{task[2]}</p>
                        </div>
                        <div className="box-delete">
                            <button 
                                className="btn-delete"
                                onClick={(e) => {
                                    handleDelete(e, index);
                                }}
                            >
                                <img src={deleteIcon} alt="button delete" />
                            </button>
                    </div>
                </div>
                ))}

            </section>
        </div>
    )
}
