import React, { useState } from 'react';

import api from '../../services/api';

import './styles.css';

export default function Form(props) {

    const [ error, setError ] = useState('');
    const [ task, setTask ] = useState('');
    const [ category, setCategory ] = useState('');

    /* Array de categorias */
    const categories = [
        ['gymImage', 'Exercicío Físico', '#F05064'],
        ['studiesImage', 'Estudos', '#4E7BFC'],
        ['origamiImage', 'Criatividade', '#5ED285' ],
        ['foodImage', 'Comida', '#FD3030' ],
        ['businessImage', 'Dinheiro', '#FD5530' ],
        ['homeImage', 'Casa', '#7C78FD' ],
        ['ideaImage', 'Ideia', '#FDC130' ],
        ['travelImage', 'Viagem', '#74DEFF' ],
        ['watchImage', 'Padrão', '#D4A269' ],
        ['petImage', 'Pet', '#9fd664' ],
        ['workImage', 'Trabalho', '#6F9379' ],
    ]

    /* Função que chama a função de adicionar nova tarefa ao Array de tarefas 
    function handleClickAdd(e) {
        e.preventDefault();
        setError('');
        if(props.caregory !== '' && props.description !== '')
            props.onChangeInput();
        else setError('Preencha todos os campos');
    }
    */

    async function handleCreateTask(e) {
        e.preventDefault();
        const data = {
            task,
            category,
        };
        if(task === '' || category === '') setError('Preencha todos os campos');
        try {
            await api.post('tarefas', data);
        } catch (err) {
            alert(err.error.data);
        }
    }

    return (
        <div className="container">
            {error 
            ?   <p className="error">{`ERRO: ${error}*`}</p>
            :   <p className="error" hidden></p>
            }
            <form className="formAddTask">
                <input 
                    type="text" 
                    name="task" 
                    id="task" 
                    placeholder="Digite aqui a tarefa*"
                    value={task}
                    onChange={  e => setTask(e.target.value) }
                />
                <div className="addTask">
                    <select 
                        name="selectCategory" 
                        className="selectCategory"
                        value={category}
                        onChange={ e => setCategory(e.target.value) }
                    >
                        <option value="">--- Selecione uma categoria --- *</option>
                        {categories.map(caregory => (
                            <option value={caregory}>
                                {caregory[1]}
                            </option> 
                        ))}
                    </select>
                    <button 
                            className="btn-addTask"
                            onClick={(e) => {  
                                handleCreateTask(e);
                            }}
                        >
                            +
                    </button>
                </div>
            </form>
        </div>
    )
}
