import React, { useState, useEffect } from 'react';

import './styles.css';

import { icons, tasksArray } from '../../services/tasks';

export default function Main() {

    const [ iconsCaregory, setIconsCaregory ] = useState([]);
    const [ categories, setCategories] = useState([]);

    const [ taskDescription, setTaskDescription] = useState('');
    const [ taskCategory, setTaskCategory ] = useState('');


    function handleAddTask(e) {
        e.preventDefault();
        const arrayCaregory = taskCategory.split(',');
        tasksArray.push([tasksArray.length + 1, arrayCaregory[0], taskDescription, arrayCaregory[1]]);
        console.log(taskDescription);
        console.log(taskCategory[0]);
        setTaskDescription('');
        setTaskCategory('');
    }

    useEffect( () => {
        setIconsCaregory(...iconsCaregory, icons);
        setCategories([
            [iconsCaregory[0], 'Exercicío Físico', '#F05064'],
            [iconsCaregory[1], 'Estudos', '#4E7BFC'],
            [iconsCaregory[2], 'Criatividade', '#5ED285' ],
            [iconsCaregory[3], 'Comida', '#FD3030' ],
            [iconsCaregory[4], 'Dinheiro', '#FD5530' ],
            [iconsCaregory[5], 'Casa', '#7C78FD' ],
            [iconsCaregory[6], 'Ideia', '#FDC130' ],
            [iconsCaregory[7], 'Viagem', '#74DEFF' ],
            [iconsCaregory[8], 'Padrão', '#D4A269' ],
            [iconsCaregory[9], 'Pet', '#90FF74' ],
            [iconsCaregory[10], 'Trabalho', '#6F9379' ],
        ]);
    },[iconsCaregory])

    return (
        <div className="container">
            <form className="formAddTask">
                <select 
                    name="selectCategory" 
                    className="selectCategory"
                    value={taskCategory}
                    onChange={ e => setTaskCategory(e.target.value)}
                >
                    <option value="0">--- Selecione uma categoria ---</option>
                    {categories.map(caregory => (
                        <option value={[caregory]}>
                            {caregory[1]}
                        </option> 
                    ))}
                </select>
                <div className="inputAdd">
                    <input 
                        type="text" 
                        name="task" 
                        id="task" 
                        placeholder="Digite aqui a tarefa"
                        value={taskDescription}
                        onChange={ e => setTaskDescription(e.target.value)}
                    />
                    <button 
                        className="btn-addTask"
                        onClick={(e) => { handleAddTask(e) }}
                    >
                        +
                    </button>
                </div>
            </form>
        </div>
    )
}
