import React, { useState } from 'react';

import './styles.css';

export default function Form(props) {

    const categories = [
        ['gymImage', 'Exercicío Físico', '#F05064'],
        ['studiesImage', 'Estudos', '#4E7BFC'],
        ['origamiImage', 'Criatividade', '#5ED285' ],
        ['foodImage', 'Comida', '#FD3030' ],
        ['businessImage', 'Dinheiro', '#FD5530' ],
        ['homeImage', 'Casa', '#7C78FD' ],
        ['ideaImage', 'Ideia', '#FDC130' ],
        ['travelImage', 'Viagem', '#74DEFF' ],
        ['watchImage', 'Padrão', '#D4A269' ],
        ['petImage', 'Pet', '#90FF74' ],
        ['workImage', 'Trabalho', '#6F9379' ],
    ]

    const [ error, setError ] = useState('');

    function handleClickAdd(e) {
        e.preventDefault();
        if(props.caregory !== '' && props.description !== '')
            props.onChangeInput();
        else setError('Preencha todos os campos*');
    }

    return (
        <div className="container">
            <form className="formAddTask">
                <select 
                    name="selectCategory" 
                    className="selectCategory"
                    value={props.category}
                    onChange={ e => props.setCategory(e.target.value) }
                >
                    <option value="0">--- Selecione uma categoria ---*</option>
                    {categories.map(caregory => (
                        <option value={caregory}>
                            {caregory[1]}
                        </option> 
                    ))}
                </select>
                <div className="inputAdd">
                    <input 
                        type="text" 
                        name="task" 
                        id="task" 
                        placeholder="Digite aqui a tarefa*"
                        value={props.description}
                        onChange={  e => props.setDescription(e.target.value) }
                    />
                    <button 
                        className="btn-addTask"
                        onClick={(e) => {  
                            handleClickAdd(e);
                        }}
                    >
                        +
                    </button>
                </div>
            </form>
        </div>
    )
}
