import React, { useEffect, useState } from 'react';

import './styles.css';

import deleteIcon from '../../assets/images/icons/delete.png';

export default function Main(props) {

    const [ tasks, setTasks ] = useState([]);

    function handleDelete(e, id, item) {
        e.preventDefault();
        const i = document.getElementById(`box-${item}`);
        i.classList.add('deleteAnimation');
        setTimeout(() => {
            const index = tasks.indexOf(tasks[id])
            let newArray = [...tasks];
            newArray.splice(index, 1);
            setTasks(newArray);
        }, 900)
    }

    useEffect(() => {
        //setTasks(...tasks, headline);
        setTasks([])
    }, []);

    return (
        <div className="container">
            <section id="list" className="container">

                {tasks.map( (task, index) => (
                    <div 
                        className='box' 
                        key={task[0]}
                        id={`box-${task[0]}`}
                    >
                        <div className="box-header" style={{background: task[3]}}>
                            <img src={task[1]} alt='studiesImage' />
                        </div>
                        <div className="box-body">
                            <p>{task[2]}</p>
                        </div>
                        <div className="box-delete">
                            <button 
                                className="btn-delete"
                                onClick={(e) => {
                                    handleDelete(e, index, task[0]);
                                }}
                            >
                                <img src={deleteIcon} alt="button delete" />
                            </button>
                    </div>
                </div>
                ))}

            </section>
        </div>
    )
}
