import React, { useState } from 'react';

import './styles.css';
import { useEffect } from 'react';

export default function Form({ headline, onChangeInput, setH }) {

    const categories = [
        ['gymImage', 'Exercicío Físico', '#F05064'],
        ['studiesImage', 'Estudos', '#4E7BFC'],
        ['origamiImage', 'Criatividade', '#5ED285' ],
        ['foodImage', 'Comida', '#FD3030' ],
        ['businessImage', 'Dinheiro', '#FD5530' ],
        ['homeImage', 'Casa', '#7C78FD' ],
        ['ideaImage', 'Ideia', '#FDC130' ],
        ['travelImage', 'Viagem', '#74DEFF' ],
        ['watchImage', 'Padrão', '#D4A269' ],
        ['petImage', 'Pet', '#90FF74' ],
        ['workImage', 'Trabalho', '#6F9379' ],
    ]

    const [ taskDescription, setTaskDescription] = useState('');
    const [ taskCategory, setTaskCategory ] = useState('');

    function handleAddTask(e) {
        e.preventDefault();
        setTaskDescription('');
        setTaskCategory('');
    }

    const [ tasks, setTask] = useState('');

    useEffect(() => {
        setTask(headline);
    }, [onChangeInput])

    return (
        <div className="container">
            <form className="formAddTask">
                <select 
                    name="selectCategory" 
                    className="selectCategory"
                    value={taskCategory}
                    onChange={ e => setTaskCategory(e.target.value)}
                >
                    <option value="0">--- Selecione uma categoria ---</option>
                    {categories.map(caregory => (
                        <option value={caregory}>
                            {caregory[1]}
                        </option> 
                    ))}
                </select>
                <div className="inputAdd">
                    <input 
                        type="text" 
                        name="task" 
                        id="task" 
                        placeholder="Digite aqui a tarefa"
                        value={taskDescription}
                        onChange={ e => setTaskDescription(e.target.value)}
                    />
                    <button 
                        className="btn-addTask"
                        onClick={(e) => {  
                            e.preventDefault();
                            onChangeInput(); 
                        }}
                    >
                        +
                    </button>
                </div>
            </form>
        </div>
    )
}
