import React, { useState, useEffect } from 'react';

import api from '../../services/api';

import './styles.css';

import deleteIcon from '../../assets/images/icons/delete.png';

import gymImage from '../../assets/images/typeIcons/gym.png';
import studiesImage from '../../assets/images/typeIcons/studies.png';
import origamiImage from '../../assets/images/typeIcons/origami.png';
import foodImage from '../../assets/images/typeIcons/food.png';
import businessImage from '../../assets/images/typeIcons/business.png';
import homeImage from '../../assets/images/typeIcons/home.png';
import ideaImage from '../../assets/images/typeIcons/idea.png';
import travelImage from '../../assets/images/typeIcons/travel.png';
import watchImage from '../../assets/images/typeIcons/watch.png';
import petImage from '../../assets/images/typeIcons/pet.png';
import workImage from '../../assets/images/typeIcons/work.png';

export default function Main(props) {

    const [ tasks, setTasks ] = useState([]);

    /* Função que deleta a tarefa especifica do array e adiciona a animação nessa tarefa 
    function handleDelete(e, id, item) {
        e.preventDefault();
        const i = document.getElementById(`box-${item}`);
        i.classList.add('deleteAnimation');
        setTimeout(() => {
            const index = props.tasks.indexOf(props.tasks[id])
            let newArray = [...props.tasks];
            newArray.splice(index, 1);
            props.setTasks(newArray);
        }, 900)
    }*/

    function handleDelete(e, index){
        e.preventDefault();
        const i = document.getElementById(`box-${index}`);
        i.classList.add('deleteAnimation');
        setTimeout(() => {
            try {
                api.delete(`/tarefas/${index}`)
            } catch (err) {
                alert(err.data);
            }
        }, 900);
    }

    function handleCategory(category) {
        if(array[0] === 'gymImage') categoryImage = gymImage;
        else if(array[0] === 'studiesImage') categoryImage = studiesImage;
        else if(array[0] === 'origamiImage') categoryImage = origamiImage;
        else if(array[0] === 'foodImage') categoryImage = foodImage;
        else if(array[0] === 'businessImage') categoryImage = businessImage;
        else if(array[0] === 'homeImage') categoryImage = homeImage;
        else if(array[0] === 'ideaImage') categoryImage = ideaImage;
        else if(array[0] === 'travelImage') categoryImage = travelImage;
        else if(array[0] === 'watchImage') categoryImage = watchImage;
        else if(array[0] === 'petImage') categoryImage = petImage;
        else if(array[0] === 'workImage') categoryImage = workImage;
        return { categoryImage, categoryBg}
    }

    useEffect(() => {
        api.get('tarefas')
        .then(response => {
            setTasks(response.data)
        });
    }, [handleDelete]);

    return (
        <div className="container">
            <section id="list" className="container">
                {tasks.map( task => (
                    <div 
                        className='box' 
                        key={task._id}
                        id={`box-${task._id}`}
                    >
                        <div className="box-header" style={{background: 'red'}}>
                            <img src={studiesImage} alt='studiesImage' />
                        </div>
                        <div className="box-body">
                            <p>{task.tarefa}</p>
                        </div>
                        <div className="box-delete">
                            <button 
                                className="btn-delete"
                                onClick={(e) => {
                                    handleDelete(e, task._id);
                                }}
                            >
                                <img src={deleteIcon} alt="button delete" />
                            </button>
                    </div>
                </div>
                ))}

            </section>
        </div>
    )
}
